mapper.select:
SELECT * FROM (SELECT A.*, ROWNUM AS RNUM, COUNT(*) OVER() AS TOTCNT
from(select d.did, d.reporter_id, d.reported_id,
(select uNickname from users where user_id = d.reported_id) as reported_nick, 
(select uNickname from users where user_id = d.reporter_id) as reporter_nick, d.dreason, d.ddate from declaration d, users u 
where reported_id is not null AND d.reporter_id = u.user_id ORDER BY dDate DESC) A)
WHERE RNUM BETWEEN #{ureportcri.rowStart}
AND #{ureportcri.rowEnd}

SELECT * FROM (SELECT A.*, ROWNUM AS RNUM, COUNT(*) OVER() AS TOTCNT
from(select d.did, d.board_id, d.ddate, d.dreason, u.unickname as reporter_nick from declaration d, board b,users u where not board_id is null and b.bId = d.board_id and d.reporter_id = u.user_id ORDER BY ddate DESC) A)
WHERE RNUM BETWEEN #{breportcri.rowStart}
AND #{breportcri.rowEnd}

SELECT * FROM (SELECT A.*, ROWNUM AS RNUM, COUNT(*) OVER() AS TOTCNT
from(SELECT * FROM BOARD WHERE BCATEGORY='공지' and user_id = 1 ORDER BY bWrittendate DESC) A)
WHERE RNUM BETWEEN #{Ncri.rowStart}
AND #{Ncri.rowEnd}

			SELECT * FROM (SELECT A.*, ROWNUM AS RNUM, COUNT(*) OVER() AS TOTCNT
from(select paId, paInfo,point_io,totalPoint,paDate FROM payment p, users u
			WHERE p.user_id = u.user_id and p.user_id = #{user_id} ORDER BY paDate DESC) A)
			WHERE RNUM BETWEEN #{poCri.rowStart}
			AND #{poCri.rowEnd}


		SELECT * FROM (SELECT A.*, ROWNUM AS RNUM, COUNT(*) OVER() AS TOTCNT
from(select s.sId, s.sDate, s.user_id, s.class_id, c.cTitle, u.uNickname as tester FROM SCRAP s, CLASS c, USERS u
			WHERE c.cId = s.class_id AND s.user_id = u.user_id AND s.user_id= #{user_id}
		ORDER BY s.sDate DESC) A)
		WHERE RNUM BETWEEN #{scrapcri.rowStart}
		AND #{scrapcri.rowEnd}

		SELECT * FROM (SELECT A.*, ROWNUM AS RNUM, COUNT(*) OVER() AS TOTCNT
			from(select rv.rvId, e.eid,e.etitle as ename, rv.rvwrittendate, rv.rvcomment, rv.rvscore from eating e, review rv where e.eid = rv.eating_id and rv.user_id = #{user_id}
            ORDER BY rvWrittendate DESC) A)
			WHERE RNUM BETWEEN #{myreviewcri.rowStart} AND #{myreviewcri.rowEnd}
